//nolint:forcetypeassert,varnamelen,revive,exhaustruct // we don't care about these linters in test cases
package gossip_test

import (
	"context"
	"encoding/json"
	"testing"

	libp2p "github.com/libp2p/go-libp2p"
	"github.com/libp2p/go-libp2p/core/crypto"
	"github.com/libp2p/go-libp2p/p2p/net/connmgr"
	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"

	"github.com/iotaledger/hive.go/serializer/v2"
	"github.com/iotaledger/hornet/v2/pkg/metrics"
	"github.com/iotaledger/hornet/v2/pkg/model/storage"
	"github.com/iotaledger/hornet/v2/pkg/p2p"
	"github.com/iotaledger/hornet/v2/pkg/protocol/gossip"
	"github.com/iotaledger/hornet/v2/pkg/testsuite"
	iotago "github.com/iotaledger/iota.go/v3"
)

const (
	MinPoWScore     uint32 = 100
	BelowMaxDepth   uint8  = 15
	ProtocolVersion uint8  = 99
)

func TestMessageProcessorEmit(t *testing.T) {
	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

	te := testsuite.SetupTestEnvironment(t, &iotago.Ed25519Address{}, 0, ProtocolVersion, BelowMaxDepth, MinPoWScore, false)
	defer te.CleanupTestEnvironment(true)

	// we use Ed25519 because otherwise it takes longer as the default is RSA
	sk, _, _ := crypto.GenerateKeyPair(crypto.Ed25519, -1)

	connManager, err := connmgr.NewConnManager(
		1,
		100,
		connmgr.WithGracePeriod(0),
	)
	require.NoError(t, err)

	n, err := libp2p.New(
		libp2p.Identity(sk),
		libp2p.ConnectionManager(connManager),
	)
	require.NoError(t, err)

	serverMetrics := &metrics.ServerMetrics{}

	manager := p2p.NewManager(n)
	go manager.Start(ctx)

	service := gossip.NewService(protocolID, n, manager, serverMetrics)
	go service.Start(ctx)

	processor, err := gossip.NewMessageProcessor(te.Storage(), te.SyncManager(), gossip.NewRequestQueue(), manager, serverMetrics, te.ProtocolManager(), &gossip.Options{
		WorkUnitCacheOpts: testsuite.TestProfileCaches.IncomingBlocksFilter,
	})
	require.NoError(t, err)

	blockData := `{
		  "protocolVersion": 99,
		  "parents": [
			"0x42e53f6bc0ecaf69f0f32dfbd838a0f96396c09b92e53225784ee9d269671939",
			"0x77cfab5b59a894bd5992b303e93c126191257c025136c610dd351b06863a9ee3",
			"0x912f97dd2b76ee450bf8495f2c1e47d4255482a5d62aba3bb30e5e2555dea164",
			"0xa0fe3e8192f0bc4270dd38b3ff08c673cb2253d75891a54bbe534540ae770623"
		  ],
		  "payload": {
			"type": 5,
			"tag": "0x362527d26c54bfc00049f46049593e7d5ff4bc0856e4a235347614b72503889b",
			"data": ""
		  },
		  "nonce": "13835058055282328477"
		}
		`

	iotaBlock := &iotago.Block{}
	assert.NoError(t, json.Unmarshal([]byte(blockData), iotaBlock))

	block, err := storage.NewBlock(iotaBlock, serializer.DeSeriModePerformValidation, te.ProtocolManager().Current())
	assert.NoError(t, err)

	// should fail because parents not solid
	err = processor.Emit(block)
	assert.Error(t, err)

	// set valid parents
	iotaBlock.Parents = iotago.BlockIDs{[32]byte{}}

	// pow again, so we have a valid block
	_, err = te.PoWHandler.DoPoW(context.Background(), iotaBlock, te.ProtocolParameters().MinPoWScore, 1, nil)
	assert.NoError(t, err)

	// need to create a new block, so the iotago block is serialized again
	block, err = storage.NewBlock(iotaBlock, serializer.DeSeriModePerformValidation, te.ProtocolManager().Current())
	assert.NoError(t, err)

	// should not fail
	err = processor.Emit(block)
	assert.NoError(t, err)

	// set wrong protocol version
	iotaBlock.ProtocolVersion = 1

	// pow again, so we have a valid block
	_, err = te.PoWHandler.DoPoW(context.Background(), iotaBlock, te.ProtocolParameters().MinPoWScore, 1, nil)
	assert.NoError(t, err)

	// need to create a new block, so the iotago block is serialized again
	block, err = storage.NewBlock(iotaBlock, serializer.DeSeriModePerformValidation, te.ProtocolManager().Current())
	assert.NoError(t, err)

	// block should fail because of wrong network ID
	err = processor.Emit(block)
	assert.Error(t, err)

	// set valid protocol version again
	iotaBlock.ProtocolVersion = ProtocolVersion

	// pow again, so we have a valid block
	_, err = te.PoWHandler.DoPoW(context.Background(), iotaBlock, te.ProtocolParameters().MinPoWScore, 1, nil)
	assert.NoError(t, err)

	// need to create a new block, so the iotago block is serialized again
	block, err = storage.NewBlock(iotaBlock, serializer.DeSeriModePerformValidation, te.ProtocolManager().Current())
	assert.NoError(t, err)

	// should not fail
	err = processor.Emit(block)
	assert.NoError(t, err)

	// set wrong nonce
	iotaBlock.Nonce = 123

	// need to create a new block, so the iotago block is serialized again
	block, err = storage.NewBlock(iotaBlock, serializer.DeSeriModePerformValidation, te.ProtocolManager().Current())
	assert.NoError(t, err)

	// should fail because of wrong score
	err = processor.Emit(block)
	assert.Error(t, err)
}
